/**
 * @typedef Instance
 *
 * @param  {*}       x to be evaluated
 * @return {Boolean}
 */
export default T.refinement(T.Object, function(x) {
    return (
        T.Boolean.is(x.isRunning) &&
        T.Boolean.is(x.fulfilled) &&
        T.Boolean.is(x.isStreaming) &&
        T.Array.is(x.output) &&
        (Result.is(x.lastMatch) || T.Nil.is(x.lastMatch))
